  `method_recom_bbk` varchar(50) DEFAULT NULL COMMENT 'ББК для метод рекомендаций',
  `isbn` varchar(100) DEFAULT NULL COMMENT 'ISBN для метод рекомендаций и книг',
  `method_recom_edited` varchar(100) DEFAULT NULL COMMENT 'под редакцией для метод рекомендаций',
  `method_recom_stated` varchar(250) DEFAULT NULL COMMENT 'Для метод рекомендаций Утверждены ...(например, утверждены на Ученом Совете Казахского \r\nНИИ онкологии и радиологии, протокол №14 от 6 декабря 2012г.)',
  `method_recom_approved` varchar(250) DEFAULT NULL COMMENT 'Для метод рекомендаций Одобрены ...(например,одобрены Экспертным Советом по вопросам развития здравоохранения МЗ РК, протокол №21 от 14 декабря 2012 года.)',
  `method_recom_published_with_the_support` varchar(250) DEFAULT NULL COMMENT 'Для метод рекомендаций: при чьей поддержке изданы \r\n(например, распечатано при поддержке ТОО Юмгискор Холдинг или изданы при поддержке Представительства АО Берлин-Хеми АГ в РК)',
  `method_recom_reviewers` varchar(250) DEFAULT NULL COMMENT 'Для метод рекомендаций: рецензенты',
  `issue_id` int(11) DEFAULT NULL COMMENT 'Внешний ключ к таблице bibl_issue',
  `section_id` int(11) DEFAULT NULL,
  `file` varchar(50) DEFAULT NULL COMMENT 'Наименование pdf файла'
